mapper.select:
SELECT MEMBER_ID, MEMBER_PWD
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_ID = #{value}

SELECT COUNT(*)
FROM MEMBER
WHERE EMAIL = #{value}

SELECT COUNT(*)
FROM MEMBER
WHERE PHONE = #{value}

SELECT
COUNT(*)
FROM MEMBER WHERE MEMBER_ID = #{memberId}

